    public  void acceptAllCookies(){

        clickElement(acceptAllCookiesButton);

    }

    public  void  createNewAdress(){

        Faker faker = new Faker();

        clickElement(createNewAdressButton);

        sendKeys(adressTitleInput,"Evim");

        sendKeys(nameInput,faker.name().firstName());

        sendKeys(surnameInput,faker.name().lastName());

        sendKeys(phoneInput,faker.phoneNumber().subscriberNumber(11));

        clickElement(citiesList);

        clickElement(istanbulOption);

        clickElement(townShipList);

        clickElement(kagithaneOption);

        clickElement(districtList);

        clickElement(gurselOption);

        sendKeys(adressInput,faker.address().fullAddress());

        clickElement(saveAdressButton);

        clickElement(saveAndMoveButton);


    }

    public  void payOperations(){

        clickElement(garantiPayButton);

        clickElement(confirmAgreement);

        clickElement(payWithGarantiPayButton);

    }


    public  boolean isOnGarantiPay(){

        return  isElementVisible(timeCounterGarantiPay);

    }


